INSERT INTO ability (name, description, name_e, name_r, description_e, description_r, thumbnail, image, url) VALUES
    ('Selbstständigkeit', 'Selbstständigkeit bezieht sich auf die Fähigkeit, unabhängig zu handeln und Verantwortung für die eigenen Entscheidungen und Handlungen zu übernehmen.','','','','','assets/icons/independence.svg','',''),
    ('Teamwork', 'Teamwork bezieht sich auf die Fähigkeit, effektiv in einer Gruppe zu arbeiten. Teamwork erfordert Zusammenarbeit, Kommunikation und Kompromissbereitschaft, um gemeinsame Ziele zu erreichen.','','','','','assets/icons/teamwork.svg','',''),
    ('Logisches Denken', 'Logisches Denken bezieht sich auf die Fähigkeit, komplexe Probleme zu analysieren und zu lösen, indem man rationale und systematische Schritte unternimmt.','','','','','assets/icons/logic.svg','',''),
    ('Kritikfähigkeit', 'Kritikfähigkeit bezieht sich auf die Fähigkeit, konstruktive Kritik zu akzeptieren und davon zu lernen. Kritikfähigkeit erfordert Offenheit und die Bereitschaft, aus Fehlern zu lernen.','','','','','assets/icons/criticism.svg','',''),
    ('Kreativität', 'Kreativität bezieht sich auf die Fähigkeit, neue und originelle Ideen zu entwickeln und umzusetzen. Kreativität kann auch helfen, Probleme auf unkonventionelle Weise zu lösen.','','','','','assets/icons/creativity.svg','',''),
    ('Kommunikation', 'Kommunikation bezieht sich auf die Fähigkeit, effektiv zu sprechen und zuzuhören, um Botschaften zu vermitteln und zu verstehen. Kommunikation ist auch im täglichen Leben wichtig.','','','','','assets/icons/communication.svg','',''),
    ('Lernfähigkeit', 'Lernfähigkeit bezieht sich auf die Fähigkeit, schnell und effektiv neue Informationen und Fähigkeiten zu erwerben. Lernfähigkeit erfordert Offenheit und die Bereitschaft, aus Erfahrungen und Fehlern zu lernen.','','','','','assets/icons/learning.svg','',''),
    ('Organisation', 'Organisation bezieht sich auf die Fähigkeit, effektiv Zeit, Ressourcen und Aufgaben zu planen und zu verwalten. Organisation kann auch helfen, den Alltag effektiver zu gestalten.','','','','','assets/icons/organization.svg','',''),
    ('Empathie', 'Die Fähigkeit, sich in andere Menschen hineinzuversetzen und deren Gefühle zu verstehen und nachzuvollziehen.','','','','','assets/icons/empathy.svg','',''),
    ('Geduld', 'Die Fähigkeit, in schwierigen oder herausfordernden Situationen ruhig zu bleiben und darauf zu vertrauen, dass sich die Dinge zum Guten wenden werden. Geduld hilft dabei, auch langfristige Ziele zu verfolgen und Hindernisse zu überwinden.','','','','','assets/icons/patience.svg','','');
